pt type=\"text/javascript\">\r\n// const\r\nvar AUTO_SLIDE_TIMEOUT = 3000; 		\r\nvar SLIDE_SPEED = 10;          		\r\nvar SLIDE_INTERVAL = 10; 			\r\nvar OPACITY_INTERVAL = 10; 			\r\nvar SLIDE_OPAACUTY = 0.2; 			\r\n\r\n\r\nvar HIDE_ONLOAD = 500;				\r\nvar HIDE_ORIENT = 100;				\r\n\r\n\r\nvar SLIDE_LEFT   = -120;\r\nvar SLIDE_CENTER = 0;\r\nvar SLIDE_RIGHT  = 120;\r\n\r\nvar RIGHT_MOVE   = 1;				\r\nvar STOP         = 0;				\r\nvar LEFT_MOVE    = -1;				\r\n// opacity\r\nvar VISIBLE = 1.0;					\r\nvar UNVISIBLE = 0;					\r\n\r\n\r\n\r\n/**\r\n * global\r\n *\r\n */\r\n// stat\r\nvar slide_stat_  = STOP;			\r\n\r\n// timer\r\nvar slide_timer_   = 0;				\r\nvar opacity_timer_ = 0;				\r\nvar auto_timer_    = 0;\r\n\r\n// news_id\r\nvar crtnews_id_ = 0;				\r\nvar nxtnews_id_ = 0;				\r\n\r\n// opacity\r\nvar crtopacity_ = 1.0; 				\r\nvar nxtopacity_ = 0; 				\r\n\r\n// position\r\nvar crt_position_ = SLIDE_CENTER;	\r\nvar nxt_position_ = SLIDE_RIGHT;	\r\n\r\n/**\r\n * \r\n * slideNextNews() \r\n * @params\r\n * @retval	void\r\n */\r\nfunction slideNextNews() {\r\n	clearTimeout( auto_timer_ );\r\n\r\n	\r\n	if( topics.length <= 1 ){\r\n		return;\r\n	}\r\n	switch( slide_stat_ ) {\r\n		case LEFT_MOVE :\r\n			return;\r\n		case RIGHT_MOVE :\r\n			\r\n			changeNewsPosition();\r\n\r\n			\r\n			changeNewsOpacity();\r\n			break;\r\n		case STOP :\r\n			crt_position_ = SLIDE_CENTER;\r\n			nxt_position_ = SLIDE_RIGHT;\r\n			break;\r\n		default :\r\n			break;\r\n	}\r\n	slide_stat_ = LEFT_MOVE;\r\n\r\n\r\n	changeNews( 1 );\r\n	crtnews_id_ = nxtnews_id_;\r\n\r\n	// timer reset\r\n	clearInterval( slide_timer_ );\r\n	clearInterval( opacity_timer_ );\r\n\r\n	// timer start\r\n	slide_timer_   = setInterval( \"secSlide( RIGHT_MOVE )\", SLIDE_INTERVAL );\r\n	opacity_timer_ = setInterval( \"changeOpacity()\", OPACITY_INTERVAL );\r\n}\r\n\r\n/**\r\n * \r\n * slidePreNews() \r\n * @params\r\n * @retval void\r\n */\r\nfunction slidePreNews() {\r\n	clearTimeout( auto_timer_ );\r\n\r\n\r\n	if( topics.length <= 1 ){\r\n		return;\r\n	}\r\n\r\n	switch( slide_stat_ ) {\r\n		case RIGHT_MOVE :\r\n			return;\r\n		case LEFT_MOVE :\r\n			\r\n			changeNewsPosition();\r\n\r\n\r\n			changeNewsOpacity();\r\n			break;\r\n		case STOP :\r\n			crt_position_ = SLIDE_CENTER;\r\n			nxt_position_ = SLIDE_LEFT;\r\n			break;\r\n		default :\r\n			break;\r\n	}\r\n\r\n	slide_stat_ = RIGHT_MOVE;\r\n\r\n	//\r\n	changeNews( -1 );\r\n	crtnews_id_ = nxtnews_id_;\r\n\r\n	// reset timer\r\n	clearInterval( slide_timer_ );\r\n	clearInterval( opacity_timer_ );\r\n\r\n	// timer start\r\n	slide_timer_   = setInterval( \"secSlide( LEFT_MOVE )\", SLIDE_INTERVAL );\r\n	opacity_timer_ = setInterval( \"changeOpacity()\", OPACITY_INTERVAL );\r\n}\r\n\r\nvar crtbox;\r\nvar nxtbox;\r\n\r\nfunction secSlide( dir ) {\r\n\r\n	// \r\n	if ( dir == RIGHT_MOVE ) {\r\n		crt_position_ -= SLIDE_SPEED; \r\n		nxt_position_ -= SLIDE_SPEED; \r\n	}\r\n	if ( dir == LEFT_MOVE ) {\r\n		crt_position_ += SLIDE_SPEED; \r\n		nxt_position_ += SLIDE_SPEED; \r\n	}\r\n\r\n	// \r\n	setCrtNewsPosition();\r\n	setNextNewsPosition();\r\n\r\n	if ( nxt_position_ == SLIDE_CENTER ) {\r\n		slideEnd();\r\n	}\r\n}\r\n\r\n\r\nfunction changeOpacity(){\r\n\r\n	\r\n	fadeOutCrtNews();\r\n	fadeInNextNews();\r\n\r\n	\r\n	setOpacity();\r\n\r\n	// fade end\r\n	if( nxtopacity_ >= VISIBLE ) {\r\n		clearInterval( opacity_timer_ );\r\n	}\r\n}\r\n\r\n\r\nfunction changeNewsPosition(){\r\n	tmp_percent = crt_position_;\r\n	crt_position_ = nxt_position_;\r\n	nxt_position_ = tmp_percent;\r\n}\r\n\r\n\r\nfunction changeNewsOpacity(){\r\n	tmp = crtopacity_;\r\n	crtopacity_ = nxtopacity_;\r\n	nxtopacity_ = tmp;\r\n\r\n	setOpacity();\r\n}\r\n\r\n\r\nfunction setCrtNewsPosition(){\r\n	crtbox.style.left = crt_position_ + \"%\"; // \r\n}\r\n\r\n\r\nfunction setNextNewsPosition(){\r\n	nxtbox.style.left = nxt_position_ + \"%\"; // \r\n}\r\n\r\n\r\nfunction fadeOutCrtNews(){\r\n	crtopacity_ -= SLIDE_OPAACUTY; \r\n}\r\n\r\n\r\nfunction setOpacity(){\r\n	crtbox.style.opacity = crtopacity_; \r\n	nxtbox.style.opacity = nxtopacity_; \r\n}\r\n\r\n\r\nfunction fadeInNextNews(){\r\n	nxtopacity_ += SLIDE_OPAACUTY;\r\n}\r\n\r\n\r\nfunction slideEnd(){\r\n\r\n	slide_stat_ = STOP;\r\n\r\n	\r\n	crtopacity_ = VISIBLE;\r\n	nxtopacity_ = UNVISIBLE;\r\n\r\n	clearInterval( slide_timer_ );\r\n\r\n	\r\n	clearTimeout( auto_timer_ );\r\n	autoSlideStart();\r\n}\r\n\r\n\r\nfunction autoSlideStart() {\r\n	auto_timer_ = setTimeout( \"slideNextNews()\", AUTO_SLIDE_TIMEOUT );\r\n}\r\n\r\n\r\nfunction changeNews( n ) {\r\n\r\n	nxtnews_id_ = crtnews_id_ + n;\r\n\r\n	if ( nxtnews_id_ >= topics.length ) {\r\n		nxtnews_id_ = 0;\r\n	} else  if ( nxtnews_id_ < 0 ) {\r\n		nxtnews_id_ = topics.length - 1;\r\n	}\r\n\r\n	// make link\r\n	topics[crtnews_id_][\'news_title\'] = makeNewsTitle( topics[crtnews_id_][\'link\'] , topics[crtnews_id_][\'title\'] );\r\n	topics[nxtnews_id_][\'news_title\'] = makeNewsTitle( topics[nxtnews_id_][\'link\'] , topics[nxtnews_id_][\'title\'] );\r\n\r\n	// set current News\r\n	ctg     =  topics[crtnews_id_][\'ctg\'] ;\r\n	title   = topics[crtnews_id_][\'news_title\'];\r\n	pubDate = topics[crtnews_id_][\'pubDate\'] ;\r\n	img =  topics[crtnews_id_][\'img\'] ;\r\n	setCrtNews( ctg, title, pubDate,img );\r\n\r\n	// set next News\r\n	ctg     =  topics[nxtnews_id_][\'ctg\'] ;\r\n	title   = topics[nxtnews_id_][\'news_title\'];\r\n	pubDate = topics[nxtnews_id_][\'pubDate\'] ;\r\n	img =  topics[nxtnews_id_][\'img\'] ;\r\n	setNxtNews( ctg, title, pubDate,img );\r\n}\r\n\r\n\r\n\r\nfunction outputError(){\r\n	ctg     = \"　\";\r\n	title   = \'<a href=\"3g.curead.cn\">推荐</a>\';\r\n	pubDate = \"　\";\r\n	img = \" \";\r\n	setCrtNews( ctg, title, pubDate, img );\r\n	setNxtNews( ctg, title, pubDate, img );\r\n}\r\n\r\n\r\n\r\n\r\nfunction setCrtNews( ctg, title, pubDate, img ){\r\n\r\n	document.getElementById(\'currentNewsCategory\').innerHTML = ctg;\r\n	document.getElementById(\'currentNewsTitle\').innerHTML 	 = title;\r\n	document.getElementById(\'currentNewsDate\').innerHTML 	 = pubDate;\r\n	document.getElementById(\'currentImg\').innerHTML 	 = \'<img src=\"\'+img+\'\" alt=\"\" />\';\r\n}\r\n\r\nfunction setNxtNews( ctg, title, pubDate, img ){\r\n\r\n	document.getElementById(\'nextNewsCategory\').innerHTML = ctg;\r\n	document.getElementById(\'nextNewsTitle\').innerHTML    = title;\r\n	document.getElementById(\'nextNewsDate\').innerHTML  	  = pubDate;\r\n	document.getElementById(\'nextImg\').innerHTML 	 = \'<img src=\"\'+img+\'\" alt=\"\" />\';\r\n}\r\n\r\n\r\n\r\n\r\nfunction makeNewsTitle( url, title ){\r\n	return \'<a href =\"\' +  url  + \'\">\' + title + \'</a>\';\r\n}\r\n\r\n\r\n\r\n/**\r\n *\r\n * URL\r\n * FIXME \r\n *\r\n * @params msec : \r\n * @retval void \r\n */\r\nfunction hideURL( msec ) {\r\n	var timer = 0;\r\n	function hideIfComplete() {\r\n		var comp = true;\r\n		for (i=0; i<document.images.length; i++) {\r\n			if(!document.images[i].complete) {\r\n				comp = false;\r\n			}\r\n		}\r\n		if ( comp == true ) {\r\n			window.clearInterval( timer );\r\n			window.setTimeout( scrollTo, 100, 0, 1 );\r\n		}\r\n	}\r\n	timer = setInterval( hideIfComplete, msec );\r\n}\r\n\r\n/**\r\n * \r\n * onload \r\n *\r\n * @params\r\n * @retval void\r\n */\r\nfunction initLoad() {\r\n	if( typeof topics == \"undefined\" || topics.length == 0 ){\r\n		outputError();\r\n	} else {\r\n		changeNews( 0 );\r\n	}\r\n	autoSlideStart();\r\n	hideURL( HIDE_ONLOAD );\r\n\r\n	setElement();\r\n}\r\n\r\n\r\n\r\n\r\n\r\n/**\r\n * elementId \r\n *\r\n * @params\r\n * @retval void\r\n */\r\nfunction setElement(){\r\n	crtbox = document.getElementById( \'currentTopic\' );\r\n	nxtbox = document.getElementById( \'nextTopic\' );\r\n}\r\n\r\nfunction updateOrientation() {\r\n	var orientation = window.orientation;\r\n	switch(orientation) {\r\n		case -90:\r\n		case 90:\r\n			document.body.setAttribute(\"id\",\"portrait\");\r\n			break;\r\n		default:\r\n			document.body.setAttribute(\"id\",\"portrait\");\r\n			break;\r\n	}\r\n}\r\n\r\n/**\r\n 